import React from 'react';
import './App.css';
import axios from 'axios';

function requestToken(login, password) {
  const response = axios.post(
    'localhost:8080/login',
    { "login": {login}, 
      "paswword": {password}
    },
  )
}

function Drugs() {
  const [login, setLogin] = React.useState("")
  const [password, setPassword] = React.useState("")

  const sendInfo = async function () {
    const data = requestToken(login, password);
    return data;
  }

  return (
    <div className="Register">
      <form>
        <label for="choose" className="text">Login</label>
        <input type="text" value={login} onChange={setLogin} required></input>
      </form>
      <form>
        <label for="choose" className="text">password</label>
        <input value={password} onChange={setPassword} required></input>
      </form>
      <button onClick={sendInfo}>Envoyer</button>
    </div>
  );
}

export default Drugs;
